http-request.set-body:
@{
    var args = context.GraphQL.Arguments;

    int first = 50;
    string after = null;
    JToken filter = null;
    JToken orderBy = null;

    try { first   = (int)args["first"];                  } catch {}
    try { after   = (string)args["after"];                } catch {}
    try { filter  = JToken.FromObject(args["filter"]);   } catch {}
    try { orderBy = JToken.FromObject(args["orderBy"]);  } catch {}

    var variables = new JObject
    {
        ["first"]   = first,
        ["after"]   = after,
        ["filter"]  = filter,
        ["orderBy"] = orderBy
    };

    var payload = new JObject
    {
        ["query"] = @"
            query (
              $first: Int
              $after: String
              $filter: UNION_REF_COUNTRY_AREAFilterInput
              $orderBy: UNION_REF_COUNTRY_AREAOrderByInput
            ) {
              uNION_REF_COUNTRY_AREAs(
                first: $first
                after: $after
                filter: $filter
                orderBy: $orderBy
              ) {
                items {
                  ISO3
                  COUNTRY_COMM_ZH
                  COUNTRY_COMM_EN
                  AREA_ID
                  AREA_NM
                  ROW
                  AREA_sort
                }
                endCursor
                hasNextPage
              }
            }",
        ["variables"] = variables
    };

    return payload.ToString();
}
