http-request.set-body:
@{
    var args = context.GraphQL.Arguments;

    int first = 50;
    string after = null;
    JToken filter = null;
    JToken orderBy = null;

    try { first   = (int)args["first"];                  } catch {}
    try { after   = (string)args["after"];                } catch {}
    try { filter  = JToken.FromObject(args["filter"]);   } catch {}
    try { orderBy = JToken.FromObject(args["orderBy"]);  } catch {}

    var variables = new JObject
    {
        ["first"]   = first,
        ["after"]   = after,
        ["filter"]  = filter,
        ["orderBy"] = orderBy
    };

    var payload = new JObject
    {
        ["query"] = @"
            query (
              $first: Int
              $after: String
              $filter: UNION_REF_HSCODEFilterInput
              $orderBy: UNION_REF_HSCODEOrderByInput
            ) {
              uNION_REF_HSCODEs(
                first: $first
                after: $after
                filter: $filter
                orderBy: $orderBy
              ) {
                items {
                  Report_ID
                  Industry_ID
                  Industry
                  HS_Code_Group
                  HS_Code
                  HS_Code_ZH
                  Unit_Name
                  Unit
                }
                endCursor
                hasNextPage
              }
            }",
        ["variables"] = variables
    };

    return payload.ToString();
}
