http-request.set-body:
@{
    var args = context.GraphQL.Arguments;

    int first = 50;
    string after = null;
    JToken filter = null;
    JToken orderBy = null;

    try { first   = (int)args["first"];                  } catch {}
    try { after   = (string)args["after"];                } catch {}
    try { filter  = JToken.FromObject(args["filter"]);   } catch {}
    try { orderBy = JToken.FromObject(args["orderBy"]);  } catch {}

    var variables = new JObject
    {
        ["first"]   = first,
        ["after"]   = after,
        ["filter"]  = filter,
        ["orderBy"] = orderBy
    };

    var payload = new JObject
    {
        ["query"] = @"
            query (
              $first: Int
              $after: String
              $filter: trade_monthly_growth_totalFilterInput
              $orderBy: trade_monthly_growth_totalOrderByInput
            ) {
              trade_monthly_growth_totals(
                first: $first
                after: $after
                filter: $filter
                orderBy: $orderBy
              ) {
                items {
                  PERIOD_MONTH
                  YEAR
                  MONTH
                  TRADE_FLOW
                  TRADE_VALUE_USD_AMT
                  TRADE_WEIGHT
                  UNIT_PRICE_USD_PER_KG
                  PREV_YEAR_TRADE_VALUE_USD_AMT
                  YOY_DELTA_TRADE_VALUE_USD_AMT
                  YOY_GROWTH_RATE_TRADE_VALUE_USD
                  PREV_MONTH_TRADE_VALUE_USD_AMT
                  MOM_DELTA_TRADE_VALUE_USD_AMT
                  MOM_GROWTH_RATE_TRADE_VALUE_USD
                  ETL_DT
                  INDUSTRY_ID
                  INDUSTRY
                  HS_CODE_GROUP
                }
                endCursor
                hasNextPage
              }
            }",
        ["variables"] = variables
    };

    return payload.ToString();
}
